import { Button, Form, Container, Card, Row, Col } from "react-bootstrap";
import React, { useState, useEffect } from "react";
import axios from "axios";

const Retrain = () => {
    const [f1, setF1] = useState(0);
    const [recall, setRecall] = useState(0);
    const [precision, setPrecision] = useState(0);
    const [file, setFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);

    // Función para obtener las métricas del modelo
    const fetchMetrics = async () => {
        try {
            const metrics = await axios.get(`http://127.0.0.1:8000/metrics`);
            const obj = metrics.data;
            setF1(obj.f1);
            setRecall(obj.recall);
            setPrecision(obj.precision);
        } catch (error) {
            console.error("Error fetching metrics:", error);
            alert("Failed to fetch metrics. Check the console for more details.");
        }
    };

    // Manejo del cambio de archivo
    const handleFileChange = (e) => {
        setFile(e.target.files ? e.target.files[0] : null);
    };

    // Manejo del envío del archivo para reentrenar el modelo
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (file) {
            setIsUploading(true);
            const formData = new FormData();
            formData.append("file", file);

            try {
                await axios.post(
                    "http://127.0.0.1:8000/retrain",
                    formData,
                    {
                        headers: {
                            "Content-Type": "multipart/form-data",
                        },
                    }
                );
                fetchMetrics();
            } catch (error) {
                console.error("Error uploading file:", error);
                alert("Error en el reentrenamiento. Por favor, revisa la consola para más detalles.");
            } finally {
                setIsUploading(false);
            }
        }
    };

    // Llamada para cargar las métricas iniciales cuando se carga el componente
    useEffect(() => {
        fetchMetrics();
    }, []);

    return (
        <Container style={{ maxWidth: "600px", margin: "0 auto", textAlign: "center" }}>
            <Card className="mb-4">
                <Card.Body>
                    <Card.Title style={{ fontWeight: "bold" }}>Métricas del modelo</Card.Title>
                    <Row className="text-center">
                        <Col>
                            <h5>Precisión:</h5>
                            <meter style={{ width: '100%', minHeight: '30px' }} value={precision}></meter>
                            <h5>{precision.toFixed(4)}</h5>
                        </Col>
                        <Col>
                            <h5>Recall:</h5>
                            <meter style={{ width: '100%', minHeight: '30px' }} value={recall}></meter>
                            <h5>{recall.toFixed(4)}</h5>
                        </Col>
                        <Col>
                            <h5>Puntaje F1:</h5>
                            <meter style={{ width: '100%', minHeight: '30px' }} value={f1}></meter>
                            <h5>{f1.toFixed(4)}</h5>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>

            <Card>
                <Card.Body>
                    <Card.Title style={{ fontWeight: "bold" }}>Reentrenar modelo</Card.Title>
                    <Card.Text>Cargar archivo CSV o XLSX</Card.Text>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Control type="file" onChange={handleFileChange} />
                        </Form.Group>
                        <Button
                            variant={isUploading ? "danger" : "success"}
                            type="submit"
                            disabled={!file || isUploading}
                        >
                            {isUploading ? "Cargando..." : "Enviar"}
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};

export default Retrain;
