import { Button, Form, Container, Card, Row, Col } from "react-bootstrap";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaCheckCircle, FaTimesCircle } from 'react-icons/fa'; // Puedes agregar otros íconos según tu preferencia

const Retrain = () => {
    const [f1, setF1] = useState(0);
    const [recall, setRecall] = useState(0);
    const [precision, setPrecision] = useState(0);
    const [file, setFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);

    // Función para obtener las métricas del modelo
    const fetchMetrics = async () => {
        try {
            const metrics = await axios.get(`http://127.0.0.1:8000/metrics`);
            const obj = metrics.data;
            setF1(obj.f1);
            setRecall(obj.recall);
            setPrecision(obj.precision);
        } catch (error) {
            console.error("Error fetching metrics:", error);
            alert("Failed to fetch metrics. Check the console for more details.");
        }
    };

    // Manejo del cambio de archivo
    const handleFileChange = (e) => {
        setFile(e.target.files ? e.target.files[0] : null);
    };

    // Manejo del envío del archivo para reentrenar el modelo
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (file) {
            setIsUploading(true);
            const formData = new FormData();
            formData.append("file", file);

            try {
                await axios.post(
                    "http://127.0.0.1:8000/retrain",
                    formData,
                    {
                        headers: {
                            "Content-Type": "multipart/form-data",
                        },
                    }
                );
                fetchMetrics();
            } catch (error) {
                console.error("Error uploading file:", error);
                alert("Error en el reentrenamiento. Por favor, revisa la consola para más detalles.");
            } finally {
                setIsUploading(false);
            }
        }
    };

    // Llamada para cargar las métricas iniciales cuando se carga el componente
    useEffect(() => {
        fetchMetrics();
    }, []);

    return (
        <Container style={{ maxWidth: "600px", margin: "0 auto", textAlign: "center" }}>
            <Card className="mb-4">
                <Card.Body>
                    <Card.Title style={{ fontWeight: "bold" }}>Métricas del modelo</Card.Title>
                    <Row className="text-center">
                        <Col>
                            <div className="metric-circle" style={{ backgroundColor: precision > 0.8 ? "#d4edda" : "#f8d7da" }}>
                                <h2>{(precision * 100).toFixed(2)}%</h2>
                                <h5>Precisión</h5>
                                {precision > 0.8 ? <FaCheckCircle color="green" size={24} /> : <FaTimesCircle color="red" size={24} />}
                            </div>
                        </Col>
                        <Col>
                            <div className="metric-circle" style={{ backgroundColor: recall > 0.8 ? "#d4edda" : "#f8d7da" }}>
                                <h2>{(recall * 100).toFixed(2)}%</h2>
                                <h5>Recall</h5>
                                {recall > 0.8 ? <FaCheckCircle color="green" size={24} /> : <FaTimesCircle color="red" size={24} />}
                            </div>
                        </Col>
                        <Col>
                            <div className="metric-circle" style={{ backgroundColor: f1 > 0.8 ? "#d4edda" : "#f8d7da" }}>
                                <h2>{(f1 * 100).toFixed(2)}%</h2>
                                <h5>Puntaje F1</h5>
                                {f1 > 0.8 ? <FaCheckCircle color="green" size={24} /> : <FaTimesCircle color="red" size={24} />}
                            </div>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>

            <Card>
                <Card.Body>
                    <Card.Title style={{ fontWeight: "bold" }}>Reentrenar modelo</Card.Title>
                    <Card.Text>Cargar archivo CSV o XLSX</Card.Text>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formFile" className="mb-3">
                            <Form.Control type="file" onChange={handleFileChange} />
                        </Form.Group>
                        <Button
                            variant={isUploading ? "danger" : "success"}
                            type="submit"
                            disabled={!file || isUploading}
                        >
                            {isUploading ? "Cargando..." : "Enviar"}
                        </Button>
                    </Form>
                </Card.Body>
            </Card>

            <style jsx>{`
                .metric-circle {
                    border-radius: 50%;
                    padding: 20px;
                    margin: 10px;
                    text-align: center;
                    border: 2px solid #ccc;
                    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
                    transition: all 0.3s ease;
                }
                .metric-circle:hover {
                    transform: translateY(-5px);
                    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
                }
            `}</style>
        </Container>
    );
};

export default Retrain;
